import React, { useState } from 'react'
import Button from '../UI/Button/Button'


const ThemeChanger = ({}) => {
   const [theme, setTheme] = useState('dark')

   console.log(theme)

  return (
    <Button 
    onClick={e => setTheme(theme === 'dark'? 'light' : 'dark')} 
    text="_" 
    />
  )
}

export default ThemeChanger
