import React from 'react'
import styles from './Input.module.scss'

const Input = ({type, value, onChange}) => {
  const {theme} = useT

  return (
    <input 
      type = {type}
      value = {value} 
      onChange = {onChange}
      className={styles.input}
      styles = {styles}
    />
  )
}

export default Input
